let tablaNueva = tabla
    switch (accion) {
      case 'Insertar':
        tablaNueva.push(paisSeleccionado)
        settabla(tablaNueva)
        setmodalInsertar(false)
        break
      case 'Editar':
        tablaNueva.map((pais) => {
          if (pais.id === paisSeleccionado.id) {
            pais.minutos = paisSeleccionado.minutos
            pais.nombre = paisSeleccionado.nombre
          }
        })
        settabla(tablaNueva)
        setmodalInsertar(false)
        break
      case 'Eliminar':
        settabla(tabla.filter((pais) => pais.id !== paisSeleccionado.id))
        setmodalEliminar(false)
        break
      default:
      //            resultadoOperacion = 99;
    }
